class ContactForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      first_name: '',
      last_name: '',
      email:''
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

 
  
  handleChange(event){
    this.setState({[event.target.name]: event.target.value});
  }
  

  handleSubmit(event) {
    alert('A name was submitted: ' + this.state.first_name + ' ' + this.state.last_name + ' ' + this.state.email);
    event.preventDefault();
  }

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <label>First name:</label>
        <input type="text" name ="first_name" value={this.state.first_name} onChange={this.handleChange} /><br/>
        <label>Last name:</label>
        <input type="text" name ="last_name" value={this.state.last_name} onChange={this.handleChange} /><br/>
        <label>Email:</label>
        <input type="text" name = "email" value={this.state.email} onChange={this.handleChange} /><br/>
        <input type="submit" value="Submit" />
      </form>
    );
  }
}

export default ContactForm;
